package com.test;


public class Abbreviation {

	public static void main(String[] args) {
		String[] inputs = {"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				"",
				""};
		for(int i=0;i<20;i=i+2) {
			System.out.println(canBeAbbreviation(inputs[i], inputs[i+1]));
		}

	}
	
	static String canBeAbbreviation(String a, String b) {
		String c = a.toUpperCase();
		int[][] dp = new int[b.length()+1][a.length()+1];
		for(int i=0; i<a.length();i++) {
			dp[0][i] = 1;
		}
		
		for(int i=0; i< b.length(); i++) {
			for(int j=0; j<a.length(); j++) {
				if(b.charAt(i) == c.charAt(j)) {
					dp[i+1][j+1] = Math.max(dp[i+1][j], dp[i][j]);
				} else {
					String s = a.substring(j, j+1);
					if(s.toUpperCase().equals(s)) {
						dp[i+1][j+1] = 0;
					} else {
						dp[i+1][j+1] = dp[i+1][j];
					}
					
				}
			}
		}
		
		if(dp[b.length()][a.length()] == 1) {
			return "YES";
		} else {
			return "NO";
		}
    }

}
